package helloworld;

public class LoopingDemo {
	// in ra gia tri int cua i tu 0 ->100

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 0; i <=10;i++) {
			System.out.println(i);
		}
		// in ra cac so chan tu 1-> 10
		System.out.println("cac so chan trong khoang 0 den 10 la:");
		for(int u = 0; u <= 10; u++ ) {
			if(u%2 == 0) {
			System.out.println(u);
		}

 }
		//cach 2
		for(int c = 0; c <=10; c +=2) {
			System.out.println(c);
		}

	}
	}
	
