package helloworld;

public class WhileDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//in ra cac so int giam dan tu 10 den 0
		int number = 10;
		while (number >= 0) {
			System.out.println("number=" + number);
			number--;
		}
		System.out.println("ket thuc");

	}

}
